		authpref, err := types.NewAuthPreference(types.AuthPreferenceSpecV2{
			SecondFactor: constants.SecondFactorOff,
		})
		require.NoError(t, err)
		_, err = s.UpsertAuthPreference(ctx, authpref)
		require.Error(t, err)
